import React, { useEffect, useState } from "react";
import { getProducts } from "../services/api";
import { useNavigate } from "react-router-dom";
import "../styles/ProductSelection.css";

const ProductSelection = () => {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await getProducts();
        setProducts(response.data);
      } catch (error) {
        setError("Failed to load products. Please refresh or try again later.");
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const handleQuantityChange = (id, quantity, price) => {
    const qty = parseInt(quantity, 10);
    if (qty < 0 || isNaN(qty)) return;
    
    setSelectedProducts(prev => ({
      ...prev,
      [id]: { 
        quantity: qty, 
        price, 
        total: qty * price,
        productName: products.find(p => p.id === id)?.productName || '' 
      }
    }));
  };

  const handleNext = () => {
    if (Object.keys(selectedProducts).length === 0) {
      setError("Please select at least one product to continue.");
      return;
    }
    localStorage.setItem("selectedProducts", JSON.stringify(selectedProducts));
    navigate("/invoice");
  };

  return (
    <div className="product-container">
      <h1>Select Products</h1>
      {loading && <p>Loading products...</p>}
      {error && <p className="error-message">{error}</p>}
      {!loading && !error && (
        <>
          <table className="product-table">
            <thead>
              <tr>
                <th>Product Name</th>
                <th>Price (₹)</th>
                <th>Quantity</th>
              </tr>
            </thead>
            <tbody>
              {products.map(product => (
                <tr key={product.id}>
                  <td>{product.productName}</td>
                  <td>₹{product.price.toFixed(2)}</td>
                  <td>
                    <input
                      type="number"
                      className="quantity-input"
                      placeholder="0"
                      min="0"
                      onChange={e => handleQuantityChange(
                        product.id, 
                        e.target.value, 
                        product.price
                      )}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
          
          <button 
            onClick={handleNext} 
            className="next-button" 
            disabled={Object.keys(selectedProducts).length === 0}
          >
            Generate Invoice
            <span className="button-icon">→</span>
          </button>
        </>
      )}
    </div>
  );
};

export default ProductSelection;
