import React, { useEffect, useState } from "react";
import { getProducts } from "../services/api";
import { useNavigate } from "react-router-dom";

const ProductSelection = () => {
  const [products, setProducts] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const navigate = useNavigate();

  useEffect(() => {
    getProducts()
      .then(response => {
        const uniqueProducts = [...new Map(response.data.map(item => [item.productName, item])).values()];
        setProducts(uniqueProducts);
      })
      .catch(error => console.error("Error fetching products:", error));
  }, []);

  const handleSearch = (event) => {
    const term = event.target.value.toLowerCase();
    setSearchTerm(term);
    const foundProduct = products.find(product => product.productName.toLowerCase().includes(term));
    setSelectedProduct(foundProduct || null);
  };

  const handleQuantityChange = (event) => {
    setQuantity(parseInt(event.target.value) || 1);
  };

  const handleNext = () => {
    if (selectedProduct) {
      const selectedData = {
        productName: selectedProduct.productName,
        price: selectedProduct.price,
        quantity,
        total: selectedProduct.price * quantity,
      };
      localStorage.setItem("selectedProducts", JSON.stringify(selectedData));
      navigate("/invoice");
    } else {
      alert("Please select a product.");
    }
  };

  return (
    <div className="product-container">
      <h1>Select Products</h1>
      <input 
        type="text" 
        placeholder="Enter Product Name" 
        value={searchTerm} 
        onChange={handleSearch} 
        className="search-input"
      />

      {selectedProduct && (
        <div className="product-details">
          <h3>{selectedProduct.productName} - â‚¹{selectedProduct.price}</h3>
          <input 
            type="number" 
            placeholder="Enter Quantity" 
            value={quantity} 
            onChange={handleQuantityChange} 
            className="quantity-input"
          />
        </div>
      )}

      <button onClick={handleNext} className="next-button">Generate Invoice</button>
    </div>
  );
};

export default ProductSelection;
