import React, { useEffect, useState } from "react";
import { getProducts } from "../services/api";
import { useNavigate } from "react-router-dom";

const ProductSelection = () => {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    getProducts()
      .then(response => setProducts(response.data))
      .catch(error => console.error("Error fetching products:", error));
  }, []);

  const handleQuantityChange = (id, quantity, price) => {
    setSelectedProducts(prev => ({
      ...prev,
      [id]: { quantity: parseInt(quantity), price, total: parseInt(quantity) * price }
    }));
  };

  const handleNext = () => {
    localStorage.setItem("selectedProducts", JSON.stringify(selectedProducts));
    navigate("/invoice");
  };

  return (
    <div>
      <h1>Select Products</h1>
      {products.map(product => (
        <div key={product.id}>
          <p>{product.productName} - â‚¹{product.price}</p>
          <input type="number" placeholder="Quantity" onChange={e => handleQuantityChange(product.id, e.target.value, product.price)} />
        </div>
      ))}
      <button onClick={handleNext}>Generate Invoice</button>
    </div>
  );
};

export default ProductSelection;
