import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const ProductSelection = () => {
  const [selectedProducts, setSelectedProducts] = useState([]);
  const navigate = useNavigate();

  const handleAddProduct = () => {
    setSelectedProducts([...selectedProducts, { name: "", quantity: 1, price: 0, total: 0 }]);
  };

  const handleChange = (index, field, value) => {
    const updatedProducts = [...selectedProducts];
    updatedProducts[index][field] = field === "quantity" || field === "price" ? parseFloat(value) : value;
    updatedProducts[index].total = updatedProducts[index].quantity * updatedProducts[index].price;
    setSelectedProducts(updatedProducts);
  };

  const handleNext = () => {
    localStorage.setItem("selectedProducts", JSON.stringify(selectedProducts));
    navigate("/invoice");
  };

  return (
    <div>
      <h1>Enter Product Details</h1>
      {selectedProducts.map((product, index) => (
        <div key={index}>
          <input 
            type="text" 
            placeholder="Product Name" 
            value={product.name} 
            onChange={e => handleChange(index, "name", e.target.value)} 
          />
          <input 
            type="number" 
            placeholder="Quantity" 
            value={product.quantity} 
            onChange={e => handleChange(index, "quantity", e.target.value)} 
          />
          <input 
            type="number" 
            placeholder="Price Per Unit" 
            value={product.price} 
            onChange={e => handleChange(index, "price", e.target.value)} 
          />
          <p>Total: â‚¹{product.total}</p>
        </div>
      ))}
      <button onClick={handleAddProduct}>Add Another Product</button>
      <button onClick={handleNext}>Generate Invoice</button>
    </div>
  );
};

export default ProductSelection;
