import React, { useEffect, useState } from "react";
import { getProducts } from "../services/api";
import { useNavigate } from "react-router-dom";
import "../styles/ProductSelection.css";

const ProductSelection = () => {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    getProducts()
      .then(response => {
        setProducts(response.data);
        setLoading(false);
      })
      .catch(() => {
        setError("Error fetching products, please try again later.");
        setLoading(false);
      });
  }, []);

  const handleQuantityChange = (id, quantity, price) => {
    const qty = parseInt(quantity, 10);
    if (qty < 0 || isNaN(qty)) return;
    
    setSelectedProducts(prev => ({
      ...prev,
      [id]: { quantity: qty, price, total: qty * price }
    }));
  };

  const handleNext = () => {
    if (Object.keys(selectedProducts).length === 0) {
      alert("Please select at least one product.");
      return;
    }
    localStorage.setItem("selectedProducts", JSON.stringify(selectedProducts));
    navigate("/invoice");
  };

  return (
    <>
      <header>
        <h1>Select Products</h1>
      </header>

      {loading && <p>Loading products...</p>}
      {error && <p className="error-message">{error}</p>}

      {!loading && !error && (
        <table className="product-table">
          <thead>
            <tr>
              <th>Product Name</th>
              <th>Price (â‚¹)</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {products.map(product => (
              <tr key={product.id}>
                <td>{product.productName}</td>
                <td>{product.price}</td>
                <td>
                  <input
                    type="number"
                    className="quantity-input"
                    placeholder="Qty"
                    min="0"
                    onChange={e => handleQuantityChange(product.id, e.target.value, product.price)}
                  />
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <button onClick={handleNext} className="next-button" disabled={Object.keys(selectedProducts).length === 0}>
        Generate Invoice
      </button>

      <footer>
        <p>&copy; 2025 Retail Billing Hub - Simplifying your transactions</p>
      </footer>
    </>
  );
};

export default ProductSelection;
