import React, { useEffect, useState } from "react";
import { getProducts } from "../services/api";
import { useNavigate } from "react-router-dom";

const ProductSelection = () => {
  const [products, setProducts] = useState([]);
  const [selectedProducts, setSelectedProducts] = useState({});
  const navigate = useNavigate();

  useEffect(() => {
    getProducts()
      .then(response => {
        console.log("Fetched Products:", response.data); // Debugging output
        setProducts(response.data);
      })
      .catch(error => console.error("Error fetching products:", error));
  }, []);

  const handleQuantityChange = (id, quantity, price) => {
    setSelectedProducts(prev => ({
      ...prev,
      [id]: { quantity: parseInt(quantity), price, total: parseInt(quantity) * price }
    }));
  };

  const handleNext = () => {
    localStorage.setItem("selectedProducts", JSON.stringify(selectedProducts));
    navigate("/invoice");
  };

  return (
    <div className="product-container">
      <h1>Select Products</h1>
      {products.length > 0 ? (
        products.map(product => (
          <div key={product.id} className="product-card">
            <label>{product.productName} - â‚¹{product.price}</label>
            <input 
              type="number" 
              placeholder="Enter Quantity" 
              className="quantity-input"
              onChange={e => handleQuantityChange(product.id, e.target.value, product.price)}
            />
          </div>
        ))
      ) : (
        <p>Loading products...</p>
      )}
      <button onClick={handleNext} className="next-button">Generate Invoice</button>
    </div>
  );
};

export default ProductSelection;
