import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/InvoiPage.css";// We'll create this CSS file

const InvoicePage = () => {
  const [invoiceData, setInvoiceData] = useState(null);
  const [isPrinting, setIsPrinting] = useState(false);

  useEffect(() => {
    const customer = JSON.parse(localStorage.getItem("customer"));
    const selectedProducts = JSON.parse(localStorage.getItem("selectedProducts"));

    const invoiceRequest = {
      customer,
      gstNumber: "29ABCDE1234F2Z5",
      date: new Date().toLocaleDateString('en-IN', {
        day: '2-digit',
        month: 'short',
        year: 'numeric'
      }),
      items: Object.keys(selectedProducts).map(productId => ({
        product: { 
          id: productId,
          productName: selectedProducts[productId].productName 
        },
        quantity: selectedProducts[productId].quantity,
        pricePerUnit: selectedProducts[productId].price,
        totalPrice: selectedProducts[productId].total
      }))
    };

    axios.post("http://localhost:8080/invoices", invoiceRequest)
      .then(response => {
        const data = response.data;
        // Calculate grand total
        data.totalPrice = data.items.reduce((sum, item) => sum + item.totalPrice, 0);
        // Calculate tax (assuming 18% GST)
        data.tax = data.totalPrice * 0.18;
        data.grandTotal = data.totalPrice + data.tax;
        setInvoiceData(data);
      })
      .catch(error => console.error("Error creating invoice:", error));
  }, []);

  const handlePrint = () => {
    setIsPrinting(true);
    setTimeout(() => {
      window.print();
      setIsPrinting(false);
    }, 500);
  };

  return (
    <div className={`invoice-container ${isPrinting ? 'printing' : ''}`}>
      {invoiceData ? (
        <>
          <div className="invoice-header">
            <div className="shop-info">
              <h1>UrbanMart</h1>
              <p>123 Trendy Street, Fashion District</p>
              <p>Bangalore, Karnataka 560001</p>
              <p>GSTIN: {invoiceData.gstNumber}</p>
            </div>
            
            <div className="invoice-meta">
              <h2>INVOICE</h2>
              <div className="invoice-number">
                <span>#INV-{Math.floor(Math.random() * 10000).toString().padStart(4, '0')}</span>
              </div>
              <p>Date: {invoiceData.date}</p>
            </div>
          </div>

          <div className="customer-details">
            <h3>Bill To:</h3>
            <p><strong>{invoiceData.customer.name}</strong></p>
            <p>{invoiceData.customer.phoneNumber}</p>
          </div>

          <div className="invoice-table-container">
            <table className="invoice-table">
              <thead>
                <tr>
                  <th className="text-left">#</th>
                  <th className="text-left">Item</th>
                  <th className="text-right">Qty</th>
                  <th className="text-right">Unit Price</th>
                  <th className="text-right">Amount</th>
                </tr>
              </thead>
              <tbody>
                {invoiceData.items.map((item, index) => (
                  <tr key={item.id}>
                    <td className="text-left">{index + 1}</td>
                    <td className="text-left">{item.product.productName}</td>
                    <td className="text-right">{item.quantity}</td>
                    <td className="text-right">₹{item.pricePerUnit.toFixed(2)}</td>
                    <td className="text-right">₹{item.totalPrice.toFixed(2)}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>

          <div className="invoice-summary">
            <div className="summary-row">
              <span>Subtotal:</span>
              <span>₹{invoiceData.totalPrice.toFixed(2)}</span>
            </div>
            <div className="summary-row">
              <span>GST (18%):</span>
              <span>₹{invoiceData.tax.toFixed(2)}</span>
            </div>
            <div className="summary-row grand-total">
              <span>Total:</span>
              <span>₹{invoiceData.grandTotal.toFixed(2)}</span>
            </div>
          </div>

          <div className="invoice-footer">
            <p>Thank you for shopping with us!</p>
            <p>Terms: Goods sold are not returnable</p>
            <div className="qr-code-placeholder"></div>
            <button className="print-button" onClick={handlePrint}>Print Invoice</button>
          </div>
        </>
      ) : (
        <div className="loading-animation">
          <div className="spinner"></div>
          <p>Generating your invoice...</p>
        </div>
      )}
    </div>
  );
};

export default InvoicePage;
