import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import "../styles/CustomerForm.css"; // Ensure the correct path

const CustomerForm = () => {
  const [name, setName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const navigate = useNavigate();

  const handleNext = async () => {
    if (!name || !phoneNumber) {
      alert("Please enter customer details.");
      return;
    }

    try {
      const response = await axios.post("http://localhost:8080/customer", { name, phoneNumber });
      console.log("Saved customer:", response.data);
      localStorage.setItem("customer", JSON.stringify(response.data)); // Save response
      navigate("/product"); // Navigate to product selection
    } catch (error) {
      alert("Error saving customer.");
    }
  };

  return (
    <div className="customer-container"> {/* Added className */}
      <h1>Enter Customer Details</h1>
      <input 
        type="text" 
        placeholder="Name" 
        value={name} 
        onChange={(e) => setName(e.target.value)} 
        className="customer-input" 
      />
      <input 
        type="text" 
        placeholder="Phone Number" 
        value={phoneNumber} 
        onChange={(e) => setPhoneNumber(e.target.value)} 
        className="customer-input" 
      />
      <button className="customer-button" onClick={handleNext}>Next</button>
    </div>
  );
};

export default CustomerForm;
